(window.webpackJsonp=window.webpackJsonp||[]).push([[46],{267:function(h,n,t){"use strict";t.r(n);var i=t(0),c=Object(i.a)({},(function(){var h=this,n=h.$createElement,t=h._self._c||n;return t("ContentSlotsDistributor",{attrs:{"slot-key":h.$parent.slotKey}},[t("h1",{attrs:{id:"hoastex"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#hoastex"}},[h._v("#")]),h._v(" HoAstex")]),h._v(" "),t("p"),t("div",{staticClass:"table-of-contents"},[t("ul",[t("li",[t("a",{attrs:{href:"#tri-ho-dom-hay-ho-khan"}},[h._v("Trị ho đờm hay ho khan")])]),t("li",[t("a",{attrs:{href:"#thoi-gian-dieu-tri-toi-da"}},[h._v("Thời gian điều trị tối đa")])]),t("li",[t("a",{attrs:{href:"#ket-hop-voi-thuoc-khac"}},[h._v("Kết hợp với thuốc khác")])])])]),t("p"),h._v(" "),t("h2",{attrs:{id:"tri-ho-dom-hay-ho-khan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#tri-ho-dom-hay-ho-khan"}},[h._v("#")]),h._v(" Trị ho đờm hay ho khan")]),h._v(" "),t("p",[h._v("Đối với thuốc hóa dược, phân định rất rõ thuốc ức chế ho (trị ho khan) và thuốc long đàm, loãng đàm (trị ho đàm), việc sử dụng từng loại thuốc phải phù hợp với từng loại triệu chứng, từng loại bệnh của người bệnh. Tuy nhiên, khác với các thuốc điều trị ho hóa dược, siro HoAstex là một thuốc trị ho có nguồn gốc dược liệu nên không có những phân định như vậy.")]),h._v(" "),t("p",[h._v("Ho đàm là sự tăng tiết dịch nhầy trong cổ họng làm ngứa họng và cản trở quá trình hô hấp gây nên ho. Nguyên nhân gây ho đàm có thể do vi khuẩn hoặc virus trong các bệnh viêm phế quản, viêm phổi… Để điều trị ho đàm nên sử dụng các thuốc long đàm, loãng đàm và kết hợp với kháng sinh nếu có nhiễm khuẩn.")]),h._v(" "),t("p",[h._v("Ho khan là ho không có đàm, thường do ngứa họng mà sinh ra ho. Nguyên nhân ho khan do dị ứng với các tác nhân của môi trường (thuốc lá, khói, bụi, phấn hoa…), do nhiễm virus cảm cúm, cảm lạnh hoặc do một số bệnh mãn tính như hen phế quản, viêm phổi tắc nghẽn mãn tính, trào ngược dạ dày mãn tính… Đa số các trường hợp ho khan không cần dùng kháng sinh do không phải nguyên nhân nhiễm vi khuẩn.\nHoAstex là một thuốc điều trị ho dùng cho trẻ sơ sinh và trẻ nhỏ trong cả 2 trường hợp: ho đàm và ho khan. Trường hợp bé của bạn, bác sĩ và dược sĩ hoàn toàn không sai khi sử dụng siro HoAstex cho bé trong lần ho khan cũng như ho đàm.")]),h._v(" "),t("p",[h._v("HoAstex với phối hợp 3 thành phần Húng chanh, Núc nác, Cineol vừa có tác dụng long đờm, giảm ho, chống dị ứng, vừa có hoạt tính kháng sinh mạnh với các vi khuẩn gây bệnh đường hô hấp.")]),h._v(" "),t("ul",[t("li",[h._v("Húng chanh có chứa tinh dầu carvanol có tác dụng kháng sinh mạnh với vi khuẩn gây bệnh đường hô hấp, dùng để điều trị ho hen, viêm phế quản, viêm họng...")]),h._v(" "),t("li",[h._v("Núc nác với thành phần chính là các flavonoid có tác dụng chống viêm, ức chế giai đoạn cấp tính của các phản ứng viêm, đồng thời lại có tính kháng sinh và chống dị ứng, dùng để điều trị các bệnh ho lâu ngày, viêm họng, khô họng.")]),h._v(" "),t("li",[h._v("Cineol là một loại tinh dầu được chiết xuất từ cây tràm có tác dụng long đờm, chống viêm, dùng để trị ho. Ngoài ra, cineol còn có tác dụng kháng khuẩn mạnh, sát trùng đường hô hấp, và kháng dị ứng, sử dụng rất hiệu quả trong các trường hợp cảm cúm.")])]),h._v(" "),t("p",[h._v("Tuy sử dụng được trong cả 2 trường hợp, siro HoAstex hiệu quả hơn trong trường hợp ho đàm. Theo phân tích ở trên, thành phần HoAstex chứa những chất có hoạt tính kháng sinh mạnh sẽ giúp diệt nguyên nhân gây bệnh hoặc ngăn ngừa bội nhiễm. Do đó, sử dụng siro HoAstex trong trường hợp ho đàm là một sự lựa chọn hoàn hảo.")]),h._v(" "),t("h2",{attrs:{id:"thoi-gian-dieu-tri-toi-da"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#thoi-gian-dieu-tri-toi-da"}},[h._v("#")]),h._v(" Thời gian điều trị tối đa")]),h._v(" "),t("p",[h._v("Theo các khuyến cáo hiện tại, thời gian điều trị đối với các bệnh thông thường như viêm phế quản, viêm họng là từ 1 đến 2 tuần. Trong một số trường hợp, thời gian điều trị có thể kéo dài hơn.")]),h._v(" "),t("p",[h._v("Trường hợp của bé nhà bạn đã điều trị được 1 tuần, hoàn toàn có thể tiếp tục sử dụng siro HoAstex thêm 1 thời gian nữa bạn nhé. Tuy nhiên, nếu sau 3 tuần mà triệu chứng ho vẫn chưa được cải thiện, bạn nên đưa bé đi tái khám để đảm bảo bé không phải ho do những bệnh khác trầm trọng hơn như: viêm phổi, hen suyễn, COPD.")]),h._v(" "),t("p",[h._v("Bạn có thể để sẵn chai thuốc HoAstex trong tủ thuốc gia đình và sử dụng mỗi khi bé chớm ho để hiệu quả điều trị ho tốt hơn mà không cần phải sử dụng kháng sinh, giúp hạn chế đề kháng kháng sinh và tránh loạn khuẩn đường ruột cho bé.")]),h._v(" "),t("h2",{attrs:{id:"ket-hop-voi-thuoc-khac"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#ket-hop-voi-thuoc-khac"}},[h._v("#")]),h._v(" Kết hợp với thuốc khác")]),h._v(" "),t("p",[h._v("Cảm ơn bạn đã tin dùng HoAstex để điều trị ho cho bé của bạn. Chúng tôi vui mừng bởi siro HoAstex luôn là sự lựa chọn đầu tiên của hàng triệu bà mẹ trong suốt hơn 20 năm qua. Với bề dày lịch sử như vậy, công ty OPC đã có đầy đủ những dữ liệu về sử dụng thuốc để khuyên bạn rằng: chẳng những không cần phải uống siro HoAstex cách xa các thuốc khác mà ngược lại nên phối hợp với các thuốc điều trị triệu chứng khác để tối ưu tác dụng, trong đó có kháng sinh.")]),h._v(" "),t("p",[h._v("Hiện nay, với việc sử dụng kháng sinh bừa bãi, tỷ lệ đề kháng kháng sinh đang tăng trưởng mãnh mẽ, và việc tìm ra một phương pháp để giảm thiểu khả năng đề kháng của vi khuẩn đang rất cấp thiết. Trong thành phần siro HoAstex có chứa tinh dầu carvanol (chiết xuất từ Húng chanh) và cineol đã được chứng minh là có hoạt tính kháng khuẩn mạnh, đây được xem là 1 nguồn kháng sinh tự nhiên rất quý giá do chưa bị đề kháng thuốc. Do đó, khi kết hợp siro HoAstex với kháng sinh (khi cần thiết) sẽ giúp tăng cường khả năng diệt khuẩn, đặc biệt là các bệnh có thể do nhiểm khuẩn đường hô hấp như viêm phế quản, viêm tiểu phế quản, viêm phổi.")]),h._v(" "),t("p",[h._v("Ngoài ra, việc kết hợp siro HoAstex với các thuốc kháng viêm, chống dị ứng hoặc giảm đau, hạ sốt cũng là một điều hết sức bình thường mà các bác sĩ chuyên nhi vẫn thường thực hiện và chưa ghi nhận bất cứ trường hợp xảy ra tương kỵ nào.")]),h._v(" "),t("p",[h._v("Tuy vậy, trong trường hợp kết hợp siro HoAstex với 1 thuốc điều trị ho khác, tốt nhất bạn nên hỏi ý kiến bác sĩ hoặc dược sĩ trước khi sử dụng để đảm bảo an toàn nhất cho bé.")])])}),[],!1,null,null,null);n.default=c.exports}}]);